        return this.$store.getters.getLanguage;
      }
    },
    methods: {
          defineTitle1(){
                let lang = this.defineLanguage;
                if (!this.largeScreen && (lang === 'ru')){
                    return this.language.ru.title1.smScreen
                }
                if (!this.largeScreen && (lang === 'en')){
                    return this.language.en.title1.smScreen
                }
                if (this.largeScreen && (lang === 'ru')){
                    return this.language.ru.title1.lgScreen
                }
                if (this.largeScreen && (lang === 'en')){
                    return this.language.en.title1.lgScreen
                }
          },
          defineTitle2(){
                let lang = this.defineLanguage;
                if (!this.largeScreen && (lang === 'ru')){
                    return this.language.ru.title2SvgSmallScreen
                }
                if (!this.largeScreen && (lang === 'en')){
                    return this.language.en.title2SvgSmallScreen
                }
                if (this.largeScreen && (lang === 'ru')){
                    return this.language.ru.title2SVG
                }
                if (this.largeScreen && (lang === 'en')){
                    return this.language.en.title2SVG
                }
          },
