            },
            animateNewPoints(stroke, pointsArr) {
                const tw = TweenMax.to(stroke, 1, {
                    attr:
                        {
                            points: this.makeNewPoints(pointsArr)
                        },
                    onUpdate: () => {
                        if (this.diagramIsAnimating === true) {
                            tw.kill();
                        }
                    },
                    onComplete: () => {
                        tw.updateTo({
                            attr:
                                {points: this.makeNewPoints(pointsArr)}}, true);
                    }
                })
            },
            transform(figure) {
                let stroke = $(figure).find('.diagram_item_stroke'),
                    pointsArr = stroke.attr('points').split(' ');
                this.currentPoints = [];
                for (let i = 0; i < pointsArr.length; i++) {
                    this.currentPoints.push(pointsArr[i])
                }

                const tw2 = TweenMax.to(stroke, 65, {
                    rotation: 360*5,
                    transformOrigin: '50% 50%',
                    ease: Linear.easeNone
                });
                // this.animateNewPoints(stroke, pointsArr);
            },
            next() {
                this.$refs.slick.next();
            },
            prev() {
              this.$refs.slick.prev();
            },

        }
    };

</script>

